  const getMemberStyle = (id: number) => {
    const hues = [30, 45, 60, 15, 75, 90]; // Bronze/gold variations
    const baseHue = hues[id % hues.length];

    return {
      accentColor: `hsl(${baseHue}, 40%, 55%)`,
      glowColor: `hsl(${baseHue}, 50%, 50%, 0.3)`,
