      y: 0, 
      transition: { 
        type: "spring", 
        stiffness: 200, 
        damping: 20
      } 
    }
  };

  // Background pattern animation for member cards
  const patternVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 0.1, transition: { duration: 1 } },
