import { useEffect, useRef, useState } from 'react';
import { motion } from 'framer-motion';

interface VideoBackgroundProps {
  videoSrc?: string;
  overlay?: boolean;
  overlayOpacity?: number;
  posterSrc?: string;
}

export function VideoBackground({
  videoSrc,
  overlay = true,
  overlayOpacity = 0.6,
  posterSrc
}: VideoBackgroundProps) {
  const videoRef = useRef<HTMLVideoElement>(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [hasError, setHasError] = useState(false);
  const [shouldLoadVideo, setShouldLoadVideo] = useState(false);

  // Force absolute URL for video source if not already absolute
  const absoluteVideoSrc = videoSrc && !videoSrc.startsWith('http') && !videoSrc.startsWith('//') 
    ? new URL(videoSrc, window.location.origin).href 
    : videoSrc;

  // Detect user preference and connection for intelligent loading
  useEffect(() => {
    const shouldAutoLoad = () => {
      // Check for reduced motion preference
      const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)').matches;
      if (prefersReducedMotion) return false;

      // Check connection quality if available
      const connection = (navigator as any).connection;
      if (connection) {
        // Don't auto-load on slow connections
        if (connection.effectiveType === 'slow-2g' || connection.effectiveType === '2g') {
          return false;
        }
        // Auto-load on fast connections
        if (connection.effectiveType === '4g') {
          return true;
        }
      }

      // Default to loading after a short delay to prioritize other content
      return true;
    };

    if (shouldAutoLoad()) {
      const timer = setTimeout(() => setShouldLoadVideo(true), 500);
      return () => clearTimeout(timer);
    }
  }, []);

  useEffect(() => {
    if (!videoRef.current || !videoSrc || !shouldLoadVideo) return;

    const video = videoRef.current;

    const handleCanPlay = () => {
      setIsLoaded(true);
      setHasError(false);
    };

    const handleError = (e: Event) => {
      console.error('Video loading failed:', absoluteVideoSrc);
      setHasError(true);
      setIsLoaded(false);
    };

    const handleLoadedMetadata = () => {
      // Start playing as soon as metadata is loaded for faster perceived loading
      video.play().catch(() => {
        // Autoplay failed, which is fine for background videos
      });
    };

    video.addEventListener('canplay', handleCanPlay);
    video.addEventListener('error', handleError);
    video.addEventListener('loadedmetadata', handleLoadedMetadata);

    // Check if video is already loaded
    if (video.readyState >= 3) {
      setIsLoaded(true);
    }

    return () => {
      video.removeEventListener('canplay', handleCanPlay);
      video.removeEventListener('error', handleError);
      video.removeEventListener('loadedmetadata', handleLoadedMetadata);
    };
  }, [shouldLoadVideo, absoluteVideoSrc, videoSrc]);

  return (
    <div className="absolute inset-0 z-0 overflow-hidden">
      {/* Placeholder gradient background - always visible */}
      <div className="absolute inset-0 bg-gradient-to-b from-dark-900 via-dark-900/80 to-dark-900"></div>

      {/* Poster image - shows immediately if provided */}
      {posterSrc && !isLoaded && (
        <motion.div 
          className="absolute inset-0 w-full h-full"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <img
            src={posterSrc}
            alt="Video thumbnail"
            className="absolute inset-0 w-full h-full object-cover"
            loading="eager"
          />
        </motion.div>
      )}

      {/* Video background - loads intelligently based on connection */}
      {videoSrc && shouldLoadVideo && (
        <motion.div 
          className="absolute inset-0 w-full h-full"
          initial={{ opacity: 0 }}
          animate={{ opacity: isLoaded ? 1 : 0 }}
          transition={{ duration: 1.2 }}
        >
          <video
            ref={videoRef}
            autoPlay
            muted
            loop
            playsInline
            preload="metadata"
            poster={posterSrc}
            className="absolute inset-0 w-full h-full object-cover"
          >
            <source src={absoluteVideoSrc} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </motion.div>
      )}

      {/* Fallback when video fails to load */}
      {hasError && !posterSrc && (
        <div className="absolute inset-0 bg-gradient-to-br from-dark-900 via-dark-800 to-dark-900"></div>
      )}

      {/* Enhanced dark overlay with bronze tint to ensure text readability and make logo pop */}
      {overlay && (
        <div 
          className="absolute inset-0 z-10"
          style={{ 
            opacity: overlayOpacity,
            background: "linear-gradient(to bottom, rgba(15, 15, 20, 0.95), rgba(15, 15, 20, 0.85), rgba(15, 15, 20, 0.95))",
            mixBlendMode: "multiply"
          }}
        ></div>
      )}

      {/* Bronze color overlay to match the site's theme */}
      {overlay && (
        <div 
          className="absolute inset-0 z-10"
          style={{ 
            opacity: 0.15,
            backgroundColor: "#a87c64",
            mixBlendMode: "color"
          }}
        ></div>
      )}

      {/* Noise texture for added visual interest */}
      <div 
        className="absolute inset-0 opacity-[0.03] z-20"
        style={{ 
          backgroundImage: 'url("data:image/svg+xml,%3Csvg viewBox=\'0 0 200 200\' xmlns=\'http://www.w3.org/2000/svg\'%3E%3Cfilter id=\'noiseFilter\'%3E%3CfeTurbulence type=\'fractalNoise\' baseFrequency=\'0.65\' numOctaves=\'3\' stitchTiles=\'stitch\'/%3E%3C/filter%3E%3Crect width=\'100%25\' height=\'100%25\' filter=\'url(%23noiseFilter)\'/%3E%3C/svg%3E")',
          backgroundRepeat: 'repeat',
          mixBlendMode: 'multiply'
        }}
      ></div>
    </div>
  );
}

client/src/components/VideoShowcase.tsx.content:
import { motion } from "framer-motion";
import { useEffect, useRef, useState } from "react";

interface VideoShowcaseProps {
  title?: string;
  subtitle?: string;
  description?: string;
  videoSrc: string;
  posterSrc?: string;
  className?: string;
  showContent?: boolean;
  variant?: "home" | "training";
}

export function VideoShowcase({
  title,
  subtitle,
  description,
  videoSrc,
  posterSrc,
  className = "",
  showContent = true,
  variant = "home"
}: VideoShowcaseProps) {
  const videoRef = useRef<HTMLVideoElement>(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [isInView, setIsInView] = useState(false);
  const [hasError, setHasError] = useState(false);
  const [shouldLoadVideo, setShouldLoadVideo] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  // Intersection Observer for lazy loading
  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsInView(true);
          // Start video loading after a brief delay to prioritize other content
          setTimeout(() => setShouldLoadVideo(true), 300);
          observer.disconnect();
        }
      },
      { threshold: 0.2, rootMargin: "50px" }
    );

    if (containerRef.current) {
      observer.observe(containerRef.current);
    }

    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    const video = videoRef.current;
    if (video && isInView && shouldLoadVideo) {
      const handleCanPlay = () => {
        setIsLoaded(true);
        setHasError(false);
        video.play().catch(() => {
          // Autoplay might be blocked, which is fine
        });
      };

      const handleError = () => {
        console.error("Video failed to load:", videoSrc);
        setHasError(true);
        setIsLoaded(false);
      };

      const handleLoadedMetadata = () => {
        // Video metadata loaded, start playing for smoother experience
        video.play().catch(() => {
          // Autoplay might be blocked
        });
      };

      video.addEventListener("canplay", handleCanPlay);
      video.addEventListener("error", handleError);
      video.addEventListener("loadedmetadata", handleLoadedMetadata);

      return () => {
        video.removeEventListener("canplay", handleCanPlay);
        video.removeEventListener("error", handleError);
        video.removeEventListener("loadedmetadata", handleLoadedMetadata);
      };
    }
  }, [isInView, shouldLoadVideo, videoSrc]);

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.8,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.6 }
    }
  };

  return (
    <section ref={containerRef} className={`py-16 md:py-24 bg-black relative overflow-hidden ${className}`}>
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-5">
        <div className="absolute inset-0" style={{
          backgroundImage: `radial-gradient(circle at 25% 25%, #a87c64 0%, transparent 50%),
                           radial-gradient(circle at 75% 75%, #a87c64 0%, transparent 50%)`
        }} />
      </div>

      <div className="container mx-auto px-4 relative z-10">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.3 }}
          className={`grid grid-cols-1 ${variant === "home" ? "lg:grid-cols-2" : "lg:grid-cols-1"} gap-12 items-center`}
        >
          {/* Content Section */}
          {showContent && (
            <motion.div variants={itemVariants} className={variant === "training" ? "text-center mb-8" : ""}>
              {subtitle && (
                <div className="flex items-center gap-2 mb-4">
                  <div className="w-12 h-0.5 bg-[#a87c64]"></div>
                  <span className="text-[#a87c64] font-rajdhani font-medium tracking-wide uppercase text-sm">
                    {subtitle}
                  </span>
                  <div className="w-12 h-0.5 bg-[#a87c64]"></div>
                </div>
              )}

              {title && (
                <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold font-orbitron text-white mb-6 leading-tight">
                  {title}
                </h2>
              )}

              {description && (
                <p className="text-gray-300 text-lg leading-relaxed font-rajdhani max-w-2xl mx-auto">
                  {description}
                </p>
              )}
            </motion.div>
          )}

          {/* Video Section */}
          <motion.div 
            variants={itemVariants}
            className={`relative ${variant === "training" ? "max-w-4xl mx-auto" : ""}`}
          >
            <div className="relative rounded-2xl overflow-hidden shadow-2xl bg-gray-900">
              {/* Poster image - shows immediately when in view */}
              {posterSrc && isInView && !isLoaded && (
                <motion.img
                  src={posterSrc}
                  alt="Video preview"
                  className="w-full h-auto object-cover"
                  style={{ aspectRatio: "16/9" }}
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.5 }}
                  loading="lazy"
                />
              )}

              {/* Loading placeholder for video */}
              {!isLoaded && shouldLoadVideo && (
                <div className="absolute inset-0 flex items-center justify-center bg-gray-900/80 z-20">
                  <div className="flex flex-col items-center gap-3">
                    <div className="w-12 h-12 border-3 border-[#a87c64] border-t-transparent rounded-full animate-spin"></div>
                    <span className="text-[#a87c64] text-sm font-rajdhani">Loading video...</span>
                  </div>
                </div>
              )}

              {/* Error state */}
              {hasError && (
                <div className="absolute inset-0 flex items-center justify-center bg-gray-900 z-20">
                  <div className="text-center">
                    <div className="text-gray-400 mb-2">Video unavailable</div>
                    <button 
                      onClick={() => {
                        setHasError(false);
                        setShouldLoadVideo(true);
                      }}
                      className="text-[#a87c64] hover:text-[#a87c64]/80 transition-colors"
                    >
                      Retry loading
                    </button>
                  </div>
                </div>
              )}

              {/* Video element - loads when shouldLoadVideo is true */}
              {shouldLoadVideo && !hasError && (
                <video
                  ref={videoRef}
                  src={videoSrc}
                  loop
                  muted
                  playsInline
                  preload="metadata"
                  poster={posterSrc}
                  className={`w-full h-auto transition-opacity duration-700 ${
                    isLoaded ? "opacity-100" : "opacity-0"
                  }`}
                  style={{ aspectRatio: "16/9" }}
                />
              )}

              {/* Elegant overlay border */}
              <div className="absolute inset-0 border border-[#a87c64]/20 rounded-2xl pointer-events-none"></div>

              {/* Corner accents */}
              <div className="absolute top-4 left-4 w-6 h-6 border-l-2 border-t-2 border-[#a87c64]"></div>
              <div className="absolute top-4 right-4 w-6 h-6 border-r-2 border-t-2 border-[#a87c64]"></div>
              <div className="absolute bottom-4 left-4 w-6 h-6 border-l-2 border-b-2 border-[#a87c64]"></div>
              <div className="absolute bottom-4 right-4 w-6 h-6 border-r-2 border-b-2 border-[#a87c64]"></div>
            </div>

            {/* Glow effect */}
            <div className="absolute -inset-4 bg-gradient-to-r from-[#a87c64]/20 via-transparent to-[#a87c64]/20 rounded-3xl blur-xl opacity-50 -z-10"></div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
}

